import styled from "styled-components";

export const Container = styled.div`
    width: 500px;
    height: 250px;
    background-color: #472643;
    box-shadow: 1px 2px 3px #f3eae8;
    border-radius: 8px;
`;

export const Button = styled.button`
    padding: 15px;
    background-color: #f2f2f2;
    color: black;
    border-radius: 5px;
`;
