import React from 'react';
import * as C from './styles';

class Button extends React.Component {
    constructor() {
        super()
        this.handleClick = this.handleClick.bind(this)

        this.state = {
            numeroDeCliquesBotaoUm: 0,
            numeroDeCliquesBotaoDois: 0,
            numeroDeCliquesBotaoTres: 0,
        }
    }

    handleClick = () => {
        return (
            this.setState((estadoAnterior, _props) => (
                { numeroDeCliquesBotaoUm: estadoAnterior.numeroDeCliquesBotaoUm +1 }
            ))
        )
    };
    handleClick2 = () => {
        return (
            this.setState((estadoAnterior, _props) => (
                { numeroDeCliquesBotaoDois: estadoAnterior.numeroDeCliquesBotaoDois +1 }
            ))
        )
    };
    handleClick3 = () => {
        return (
            this.setState((estadoAnterior, _props) => (
                { numeroDeCliquesBotaoTres: estadoAnterior.numeroDeCliquesBotaoTres +1 }
            ))
        )
    };


    render () {
        return (
          <C.Container>
              <C.Button onClick={this.handleClick} >{this.state.numeroDeCliquesBotaoUm}</C.Button>
              <C.Button onClick={this.handleClick2} > {this.state.numeroDeCliquesBotaoDois}</C.Button>
              <C.Button onClick={this.handleClick3} > {this.state.numeroDeCliquesBotaoTres}</C.Button>
              <C.Button></C.Button>
          </C.Container>
        )
    }
}

export default Button;
