import React from 'react';
import * as C from './styles';

class Button extends React.Component {
    constructor() {
        super()
        this.clickBtnOne = this.clickBtnOne.bind(this);
        this.clickBtnTwo = this.clickBtnTwo.bind(this);
        this.clickBtnTree = this.clickBtnTree.bind(this);

        this.state = {
            clickBtnOne: 0,
            clickBtnTwo: 0,
            clickBtnTree: 0
        };
    };

    handleButtonOne = () => (
        this.setState(({ clickBtnOne }) => {
            return {
                clickBtnOne: clickBtnOne + 1
            };
        })
    );

    handleButtonTwo = () => (
        this.setState(({ clickBtnTwo }) => (
            { clickBtnTwo: clickBtnTwo + 1 }
        ))
    );

    handleButtonTree = () => (
        this.setState(({ clickBtnTree }) => (
            { clickBtnTree: clickBtnTree + 1 }
        ))
    );

    getButtonColor = (num) => {
        return num % 2 === 0 ? 'green' : 'white';
    };

    render () {

        return (
          <C.Container>
              <C.Button onClick={this.clickBtnOne}
              style={{ backgroundColor: this.getButtonColor(this.clickBtnOne) }}
              >{this.clickBtnOne}</C.Button>
          </C.Container>
        )
    }
}

export default Button;
