import styled from "styled-components";

export const Container = styled.div`
    background: grey;
    width: 1200px;
    margin: auto;
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
`;

export const Card = styled.div`
    border: 0.4px solid black;
    width: 200px;
    height: 220px;
    background: teal;
    margin: 30px 0px;
`;
