import { createGlobalStyle } from 'styled-components';

const Global = createGlobalStyle`

    body {
        margin:0;
        padding: 0;
        font-family: 'Poppins', Sans-Serif;
        background-color: #f2f2f2;
    }
`;

export default Global;
