import styled from "styled-components";

export const Container = styled.div`
    background: #fff;
    width: 70%;
    margin: auto;
    display: flex;
    justify-content: space-around;
    align-items: center;
    flex-wrap: wrap;
`;

export const Card = styled.div`
    border-radius: 15px;
    border: 0.4px solid grey;
    box-shadow: 0px 0px 3px grey;
    width: 550px;
    height: 220px;
    background: teal;
    margin: 30px 0px;
    padding: 10px;
    display: flex;
    justify-content: space-around;
`;

export const Paragraph = styled.p`
    color: #fff;
    font-weight: bold;
    font-size: 20px;
    line-height: 70px;
`;
